SELECT A.ORG_ID, A.OU, A.SEGMENT1, A.TAHUN,        A.AMOUNT1,                      (SELECT PP.ENTERED_CR FROM (SELECT A.PERIOD_NAME, SUBSTR(A.PERIOD_NAME,1,3) AS PERIOD_BULAN, SUBSTR(A.PERIOD_NAME,5,2) AS PERIOD_TAHUN, SUM(A.ENTERED_CR) AS ENTERED_CR,       B.SEGMENT2,               CASE           WHEN B.SEGMENT6_DESC = 'BLOW PET' THEN                'PET'           ELSE                B.SEGMENT6_DESC       END AS SEGMENT6_DESC,       DECODE(B.SEGMENT2,'10106','152',                      '10107','161',                      '10109','170',                      '10102','82',                      '10105','85',                      '10108','86',                      '10103','83',                      '10104','84',                      '20201','192',                      '20301','210',                      '20401','228',                      '20601','252') as ORG_IDFROM   GL_JE_LINES A, COA_DESC BWHERE  A.CODE_COMBINATION_ID = B.CODE_COMBINATION_IDAND    JE_HEADER_ID IN (SELECT JE_HEADER_ID                         FROM   GL_JE_HEADERS                        WHERE  NAME LIKE '%BUDGET%'                        AND    BUDGET_VERSION_ID IN (11001, 11002, 11003, 11004, 11005)                        AND    JE_SOURCE = 'Spreadsheet')AND    B.SEGMENT5 IN (41010001, 41020001)GROUP BY A.PERIOD_NAME, B.SEGMENT2, B.SEGMENT6_DESC)PPWHERE PP.ORG_ID = A.ORG_IDAND   PP.PERIOD_BULAN = 'JAN'AND   PP.PERIOD_TAHUN = SUBSTR(A.TAHUN, 3, 2)AND   PP.SEGMENT6_DESC = A.SEGMENT1) AS TARGET1,                     A.AMOUNT2, A.TARGET_AMOUNT2 AS TARGET2,                     A.AMOUNT3, A.TARGET_AMOUNT3 AS TARGET3,       A.AMOUNT4, A.TARGET_AMOUNT4 AS TARGET4,       A.AMOUNT5, A.TARGET_AMOUNT5 AS TARGET5,       A.AMOUNT6, A.TARGET_AMOUNT6 AS TARGET6,       A.AMOUNT7, A.TARGET_AMOUNT7 AS TARGET7,       A.AMOUNT8, A.TARGET_AMOUNT8 AS TARGET8,       A.AMOUNT9, A.TARGET_AMOUNT9 AS TARGET9,       A.AMOUNT10, A.TARGET_AMOUNT10 AS TARGET10,       A.AMOUNT11, A.TARGET_AMOUNT11 AS TARGET11,       A.AMOUNT12, A.TARGET_AMOUNT12 AS TARGET12FROM   YNPDO_BUDGET_ACTUAL AWHERE  A.TAHUN = (SELECT TO_CHAR(SYSDATE, 'YYYY') FROM DUAL)ORDER BY A.OU, A.SEGMENT1 ASC